import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import { useToast } from './useToast';

function UserRegistrationPage() {
    const [userData, setUserData] = useState({ userId: ''});
    const [loginData, setLoginData] = useState({ userId: ''});
    const { showError } = useToast();
    const { showMessage } = useToast();

    const handleRegistration = () => {
        axios.post('http://localhost:8080/api/users', userData)
            .then(response => {
                
                showMessage('User registered successfully');
                console.log('User registered successfully:', response.data);
                // Optionally, you can redirect the user to another page after successful registration
            })
            .catch(error => {
                
                showError('Error registering user');
                 console.error('Error registering user:', error);
                // Handle error, display error message to user
            });
    };

    const handleLogin = () => {
        axios.post('http://localhost:8080/api/users/login', loginData)
            .then(response => {
                showMessage('User logged in successfully');
                console.log('User logged in successfully:', response.data);
                // Optionally, you can redirect the user to another page after successful login
            })
            .catch(error => {
                showError('Error logging in');
                console.error('Error logging in:', error);
                // Handle error, display error message to user
            });
    };

    return (
        <div className="user-registration-page">
            <h2>User Registration</h2>
            <input type="text" placeholder="UserId" value={userData.userId} onChange={e => setUserData({ ...userData, userId: e.target.value })} />
            <button onClick={handleRegistration}>Register</button>

            <h2>User Login</h2>
            <input type="text" placeholder="UserId" value={loginData.userId} onChange={e => setLoginData({ ...loginData, userId: e.target.value })} />
            <button onClick={handleLogin}>Login</button>
        </div>
    );
}

export default UserRegistrationPage;
