import React, { useState } from 'react';
import axios from 'axios';

function UserRegistrationPage() {
    const [userData, setUserData] = useState({ username: ''});
    const [loginData, setLoginData] = useState({ username: ''});

    const handleRegistration = () => {
        axios.post('http://localhost:8080/api/users', userData)
            .then(response => {
                console.log('User registered successfully:', response.data);
                // Optionally, you can redirect the user to another page after successful registration
            })
            .catch(error => {
                console.error('Error registering user:', error);
                // Handle error, display error message to user
            });
    };

    const handleLogin = () => {
        axios.post('http://localhost:8080/api/users/login', loginData)
            .then(response => {
                console.log('User logged in successfully:', response.data);
                // Optionally, you can redirect the user to another page after successful login
            })
            .catch(error => {
                console.error('Error logging in:', error);
                // Handle error, display error message to user
            });
    };

    return (
        <div className="user-registration-page">
            <h2>User Registration</h2>
            <input type="text" placeholder="UserId" value={userData.username} onChange={e => setUserData({ ...userData, username: e.target.value })} />
            <button onClick={handleRegistration}>Register</button>

            <h2>User Login</h2>
            <input type="text" placeholder="Username" value={loginData.username} onChange={e => setLoginData({ ...loginData, username: e.target.value })} />
            <button onClick={handleLogin}>Login</button>
        </div>
    );
}

export default UserRegistrationPage;
