import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import EventRegistrationApp from './EventRegistrationApp';
import UserRegistrationPage from './UserRegistrationPage';
import MyEventsPage from './MyEventsPage';
import HomePage from './HomePage';


function Navbar() {
  return (
    <nav className="navbar">
      <ul>
        <li>
          <Link to="/" className="nav-link">Home</Link>
        </li>
        <li>
          <Link to="/event-registration" className="nav-link">Event Registration</Link>
        </li>
        <li>
          <Link to="/user-registration" className="nav-link">User Registration</Link>
        </li>
        <li>
          <Link to="/my-events" className="nav-link">My Events</Link>
        </li>
      </ul>
    </nav>
  );
}

function App() {

  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const fetchUserId = async () => {
      // Fetch userId from localStorage or API
      const userIdFromStorage = localStorage.getItem('userId');
      setUserId(userIdFromStorage);
    };

    fetchUserId();
  }, []);

  return (
    <Router>
      <div className="App">
      <ToastContainer />
      <Navbar />
        {/* <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/event-registration">Event Registration</Link>
            </li>
            <li>
              <Link to="/user-registration">User Registration</Link>
            </li>
            <li>
              <Link to="/my-events">My Events</Link>
            </li>
          </ul>
        </nav> */}

        <Routes>
          <Route path="/event-registration" element={<EventRegistrationApp userId={userId} />} />
          <Route path="/user-registration" element={<UserRegistrationPage />} />
          <Route path="/my-events" element={<MyEventsPage userId={userId} />} />
          <Route path="/" element={<HomePage />} />
        </Routes>
      </div>
    </Router>
  );
}


export default App;
