import React from 'react';

function EventRegistrationApp({ events, selectedEvents, toggleSelectedEvent }) {
  return (
    <div className="event-registration-app">
      <div className="event-list">
        <h2>All Events</h2>
        <div className="event-cards">
          {events.map(event => (
            <div key={event.id} className={`event-card ${selectedEvents.includes(event.id) ? 'selected' : ''}`}>
              <div className="event-info">
                <div className="event-name">{event.event_name}</div>
                <div className="event-category">{event.event_category}</div>
                <div className="event-timings">{event.start_time} - {event.end_time}</div>
              </div>
              <button onClick={() => toggleSelectedEvent(event.id)}>
                {selectedEvents.includes(event.id) ? 'Deselect' : 'Select'}
              </button>
            </div>
          ))}
        </div>
      </div>
      <div className="selected-event-list">
        <h2>Selected Events</h2>
        <div className="event-cards">
          {selectedEvents.map(eventId => {
            const event = events.find(e => e.id === eventId);
            return (
              <div key={eventId} className="event-card selected">
                <div className="event-info">
                  <div className="event-name">{event.event_name}</div>
                  <div className="event-category">{event.event_category}</div>
                  <div className="event-timings">{event.start_time} - {event.end_time}</div>
                </div>
                <button onClick={() => toggleSelectedEvent(eventId)}>Deselect</button>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default EventRegistrationApp;
