import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function HomePage() {
    const [loginFormData, setLoginFormData] = useState({
        userId: '',
        password: ''
    });

    const handleLoginChange = (e) => {
        const { name, value } = e.target;
        setLoginFormData({ ...loginFormData, [name]: value });
    };

    const handleLoginSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/api/users/login', loginFormData)
            .then(response => {
                // Handle successful login (e.g., redirect to another page)
                console.log('Login successful:', response.data);
            })
            .catch(error => {
                // Handle login error
                console.error('Login error:', error);
            });
    };

    const [registerFormData, setRegisterFormData] = useState({
        // Define state for registration form fields
    });

    const handleRegisterChange = (e) => {
        // Handle change in registration form fields
    };

    const handleRegisterSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/api/users', registerFormData)
            .then(response => {
                // Handle successful registration (e.g., redirect to login page)
                console.log('Registration successful:', response.data);
            })
            .catch(error => {
                // Handle registration error
                console.error('Registration error:', error);
            });
    };

    return (
        <div className="home-page">
            <h1>Sports Day</h1>
            <div className="login-section">
                <h2 style={{ textalign: center }}>Login</h2>
                <form onSubmit={handleLoginSubmit}>
                    <input type="text" name="userId" placeholder="User ID" value={loginFormData.userId} onChange={handleLoginChange} required />
                    <button style={{ marginLeft: '10px' }} type="submit">Login</button>
                </form>
            </div>
            <div className="register-section">
                <p>Don't have an account? <Link to="/register">Register</Link></p>
                {/* Include the registration form here */}
            </div>
        </div>
    );
}

export default HomePage;
